// 获取cookie
var getCookie = function (name) {
    var arr, reg = new RegExp("(^| )" + name + "=([^;]*)(;|$)");
    if (arr = document.cookie.match(reg))
        return unescape(arr[2]);
    else
        return null;
}
// 设置cookie
var setCookie = function (c_name, value, expiredays) {
    var exdate = new Date()
    exdate.setDate(exdate.getDate() + expiredays)
    document.cookie = c_name + "=" + escape(value) +
        ((expiredays == null) ? "" : ";expires=" + exdate.toGMTString())
}

//根据某个字符串获取url参数的值
var getParameter = function (name) {
    var reg = new RegExp("(^|&)" + name + "=([^&]*)(&|$)");
    var r = window.location.search.substr(1).match(reg);
    if (r != null) return decodeURIComponent(r[2]);
    return null;
}
//获取url最后一个斜杠后面的字符串
var getLastString = function (url) {
    var index = url.lastIndexOf("/");
    var length = url.length;
    return url.substring(index + 1, length);
}
//格式化服务器返回的服务时段
var formatServeTime = function () {
    $("*[data-serverTime]").each(function (index, el) {
        var value = $(this).attr("data-serverTime").split(",");
        var text = "";
        for (var i in value) {
            text += value[i] + ":00-" + (parseInt(value[i]) + 1) + ":00 ";
        }
        $(this).html(text);
    });
}

//对含有time-format指令的标签进行格式化，使用方法如：<abbr datetime-format="yyyy-MM-dd">时间</abbr>
var formatDateTime = function () {
    $("*[datetime-format]").each(function (index, el) {
        var format = $(this).attr("datetime-format");
        var text = $(this).text().trim().replace("T", " ").replace(/-/g, "/");
        var ymdhms = text.indexOf(".") != -1 ? text.substr(0, text.indexOf(".")) : text; //删掉秒后面的小数位数
        var date = new Date(ymdhms);
        $(this).html(date.Format(format));
    });
}

//获取时间戳
var getTimestamp = function (time) {
    var text = time.replace("T", " ").replace(/-/g, "/");
    var ymdhms = text.indexOf(".") != -1 ? text.substr(0, text.indexOf(".")) : text; //删掉秒后面的小数位数
    var timestamp = new Date(ymdhms).getTime();
    return timestamp;
}

//合并json，将jsonB合并到jsonA的name字段下面
//name为新字段的名称，aID为主jsonA里的某个id字段，jsonA为主json，jsonB为将被合并的json，bID为jsonB的ID字段，bKey为jsonB里的名称字段
var mergeJson = function (name, aID, jsonA, jsonB, bID, bKey) {
    if (jsonA) {
        var jsonALength = jsonA.length,
            jsonBLength = jsonB.length;
        if (jsonALength) {
            for (var i = 0; i < jsonALength; i++) {
                for (var j = 0; j < jsonB.length; j++) {
                    if (jsonA[i][aID] == jsonB[j][bID]) {
                        jsonA[i][name] = jsonB[j];
                    }
                }
            }
        }
        return jsonA;
    }
}

//根据ID获取名字
var getNameByID = function (ID, targetJson, targetID, targetName) {
    for (var i = 0; i < targetJson.length; i++) {
        if (targetJson[i][targetID] == ID) {
            return targetJson[i][targetName];
        }
    }
}

//获取字符串第一个汉字首字母，不考虑多音字
var getFirstLetter = function (str) {
    var strChineseFirstPY = "";
    var checkCh = function (ch) {
        var uni = ch.charCodeAt(0);
        if (uni > 40869 || uni < 19968)
            return ch;
        return (strChineseFirstPY.charAt(uni - 19968));
    }
    if (typeof (str) != "string")
        throw new Error(-1, "函数makePy需要字符串类型参数!");
    var ch = str.charAt(0);
    return checkCh(ch);
}

//指定字符前插入字符串
var insertString = function (a, b, at) {
    var position = a.lastIndexOf(at);
    if (position !== -1) {
        return a.substr(0, position) + b + a.substr(position);
    }
    return "substring not found";
}

//是否为微信
var isWechat = function () {
    var ua = navigator.userAgent.toLowerCase();
    return ua.match(/MicroMessenger/i) == "micromessenger" ? true : false;
};

//是否为微博
