/*
 * fn: 传入要执行的函数
 * delay：延迟时间
 * debounce的作用是在让在用户动作停止后延迟(如 200ms)再执行回调
 */
var debounce = function (fn, delay) {
    var delay = delay || 200;
    var timer;
    return function () {
        var self = this;
        var args = arguments;
        if (timer) clearTimeout(timer);
        timer = setTimeout(function () {
            timer = null;
            fn.apply(self, args);
        }, delay);
    };
}

/*
 * fn: 传入要执行的函数
 * interval：时间间隔
 * throttle的作用是在用户动作时每隔一定时间（如 200ms）执行一次回调。
 */
var throttle = function (fn, interval) {
    var last;
    var timer;
    var interval = interval || 200;
    return function () {
        var self = this;
        var args = arguments;
        var now = +new Date();
        if (last && now - last < interval) {
            clearTimeout(timer);
            timer = setTimeout(function () {
                last = now;
                fn.apply(self, args);
            }, interval);
        } else {
            last = now;
            fn.apply(self, args);
        }
    }
}

/*
 * 显示对象或者值的类型
 * return：Object | String | Array | Number | Boolean | Null | Undefined
 */
var showObjType = function (obj) {
    var res = Object.prototype.toString.apply(obj);

    // res 的格式是 '[object 类型]',所以要取类型的字段的话从第 8 位开始
    return res.substring(8, res.length - 1);
}


/**
 * 针对换行做格式化显示
 * obj: 传入的格式话对象，这里应该是一个用jq获取的dom元素
 */
var formatBr = function (obj) {
    if (showObjType(obj) === ('Null' || 'Undefined') || obj.length === 0) {
        console.log('common.js formatBr 你传入的格式化对象：', obj)
        return obj;
    }
    introTxt = obj.text().split("\n"),
        len = introTxt.length;
    return introTxt.filter(function (item, index) {
        var trimItem = $.trim(item);
        if (trimItem.length > 0) {
            return trimItem
        }
    }).map(function (item, index) {
        return ('<p>' + item + '</p>')
    })
}

/**
 * 针对换行做格式化显示
 * obj: 传入的格式话对象
 * obj.type: 【必须】，值为 id 或者 class，如果是 =id 表示要id去获取元素，如果是class表示用类名去获取元素
 * obj.name: 必须，id名称获取类名
 * 注意：传入的元素必须只能有一个，如果是传入类名，获取到的页面元素有多个的话，只对第一个元素有效果
 */
var formatBrByOrigin = function (obj) {
    var obj = obj || {};
    if (!obj.type || !obj.name) {
        console.log('common.js formatBr 参数有误，name跟id必须，你传入的参数为：', obj);
        return false;
    }
    var el = obj.type === 'id' ? document.getElementById(obj.name) :
        obj.type === 'class' ? document.getElementsByClassName(obj.name)[0] : '';
    if (!el || el.length === 0) {
        console.log('common.js formatBr 获取到的页面元素有误，获取到的元素为：', el);
        return false;
    }
    var elTxtArr = el.innerHTML.split('\n');
    var formatTxt = elTxtArr.filter(function (item, index) {
        // 下面的正则是去掉字符串的首尾空格
        var trimItem = item.replace(/(^\s*)|(\s*$)/g, "");
        if (trimItem.length > 0) {
            return trimItem
        }
    }).map(function (item, index) {
        return ('<p>' + item + '</p>')
    }).join('')
    el.innerHTML = formatTxt;
}

/*
 * 使用相对协议
 */
var useRelativeProtocol = function (str) {
    if (!str || str.length === 0) return str
    var ret = str.split(':')
    if (ret[1]) return ret[1]
    return ret[0];
}


