/**
 * 判断数组的每一项是否是唯一项
 */
var checkIsUniqueOfArrayFileds = function (arr) {
    if (showObjType(arr) !== "Array" || arr.length === 0) return false
    var isU = true
    arr.forEach(function (ele, index, array) {
        if (array.indexOf(ele) > -1 && array.indexOf(ele) !== index) isU = false
    })
    return isU
}